(function (e) {
    "use strict";
    e(document).ready(function () {
        var t = e(window),
            n = e(".page-header");
        e(".customize .feature").on("mouseenter", function () {
            e(this).addClass("active").siblings(".feature").removeClass("active");
            var t = e(this).attr("data-feature");
            e("#" + t).parent().animate({
                opacity: 1,
                zIndex: 2
            }, 200).siblings("div").animate({
                    opacity: 0,
                    zIndex: 1
                }, 200)
        });
        var i = e("#favorites-slider");
        i.cycle({
            autoHeight: 0,
            fx: "scrollHorz",
            slides: "> div",
            timeout: 0,
            next: "#next-slide",
            prev: "#prev-slide"
        });
        var s = e("#testimonial-list").find("li"),
            o = e("#testimonial");
        e(".testimonial-faces").hover(function () {
            e(this).find(".testimonial-faces-overlay").stop().animate({
                opacity: 0
            }, 200, function () {
                e(this).css("display", "none")
            })
        }, function () {
            e(this).find(".testimonial-faces-overlay").css("display", "block").stop().animate({
                opacity: 1
            }, 200)
        });
        s.each(function () {
            e(this).css("opacity", 0);
            e(this).on("mouseenter", function () {
                var t = e(this).text(),
                    n = e(this).attr("data-source");
                e(this).stop().animate({
                    opacity: 1
                }, 50);
                o.children("p").fadeOut(200, function () {
                    e(this).text(t).fadeIn(200)
                });
                o.children("footer").fadeOut(200, function () {
                    e(this).text(n).fadeIn(200)
                })
            });
            e(this).on("mouseleave", function () {
                e(this).stop().animate({
                    opacity: 0
                }, 200)
            })
        });
        e("body").hasClass("pricing") && e(".plan-features").tooltip({
            track: !0,
            position: {
                my: "left+30 top"
            }
        })
    })
})(window.jQuery);